mapper.select:
		SELECT count(*)
    	FROM users
		WHERE  user_tel = #{user_tel}

		SELECT count(*)
    	FROM users
		WHERE  user_id = #{user_id}

SELECT count(*)
FROM users
WHERE user_id = #{user_id} and user_password = #{user_password}
and user_role = 'consumer';

SELECT user_id
FROM users
WHERE user_name = #{user_name} and user_tel = #{user_tel}

mapper.update:
UPDATE users
SET user_password = #{user_password}
WHERE user_tel = #{user_tel};

mapper.select:
SELECT count(*)
FROM seller
WHERE user_id = #{user_id} and seller_black = 0;

SELECT count(*)
FROM users
WHERE user_id = #{user_id} and user_black = 0;

SELECT count(*)
FROM seller
WHERE selling_number = #{selling_number} or seller_black = 1;
